mapper.select:
SELECT A.*, (SELECT CODENAME FROM TB_CODE WHERE CODE=A.STAT AND GROUPID='ADMSTAT') AS CODENAME, DATE_FORMAT(REG_DTTM, '%Y-%m-%d %H:%i') AS REG_DTTM1 FROM TB_BTB_ADM A WHERE 1=1 AND A.BTBS_SQ = #{btbs_sq}
AND A.REG_DTTM BETWEEN CONCAT(#{start_dt},'000000') AND CONCAT(#{end_dt},'235959')
AND A.ADM_EMAIL LIKE CONCAT('%', #{keyword}, '%') AND A.ADM_NM LIKE CONCAT('%', #{keyword}, '%')
ORDER BY ${orderBy} LIMIT ${R_PAGE} , ${R_ROW}

SELECT COUNT(*) AS CNT FROM TB_BTB_ADM A WHERE 1=1 AND A.BTBS_SQ = #{btbs_sq}
AND A.REG_DTTM BETWEEN CONCAT(#{start_dt},'000000') AND CONCAT(#{end_dt},'235959')
AND A.ADM_EMAIL LIKE CONCAT('%', #{keyword}, '%') AND A.ADM_NM LIKE CONCAT('%', #{keyword}, '%')

mapper.update:
UPDATE TB_BTB_ADM
   SET 
	   ADM_PWD       =  #{adm_pwd},
       UPT_DTTM      =  now(),
       UPT_ADM_SQ    =  #{upt_adm_sq}
WHERE ADM_SQ = #{adm_sq}

mapper.select:
SELECT * 
FROM   TB_TEST_AIR 
WHERE  1=1
AND    DATADATE=#{datadate} 
AND    SINAME=#{siname} 
AND    GUNAME=#{guname}
