mapper.select:
SELECT A.APP_SQ, A.APP_VNM, A.APP_VRS, A.APP_OS, (SELECT CODENAME FROM TB_CODE WHERE GROUPID = 'APPSTAT' AND CODE = A.STAT) AS STATCODE, DATE_FORMAT(A.REG_DTTM, '%Y-%m-%d %H:%i:%S') AS REG_DTTM FROM TB_APP A WHERE 1=1
AND REG_DTTM BETWEEN CONCAT(#{startDate},' 00:00:00') AND CONCAT(#{endDate},' 23:59:59')
AND A.APP_OS = #{appOs}
AND A.APP_VNM LIKE CONCAT('%',#{keyword},'%')
AND A.STAT = #{stat}
ORDER BY ${orderBy} LIMIT ${R_PAGE} , ${R_ROW}

SELECT COUNT(*) FROM TB_APP WHERE 1=1
AND REG_DTTM BETWEEN CONCAT(#{startDate},' 00:00:00') AND CONCAT(#{endDate},' 23:59:59')
AND APP_OS = #{appOs}
AND APP_VNM LIKE CONCAT('%',#{keyword},'%')
AND STAT = #{stat}

mapper.update:
     UPDATE TB_TUT SET
		STAT = '03',
		DEL_DTTM = now(),
		DEL_ADM_SQ = #{delAdmSq}
	WHERE TUT_SQ = #{tutSq}

mapper.insert:
      INSERT INTO TB_TUT
      (
		TUT_SQ, TUT_NM, TUT_CONT,
		STAT, REG_DTTM, REG_ADM_SQ
      )
      VALUES
      (
      	NEXTVAL('TUT_SQ'), #{tutNm}, #{tutCont},
		#{stat}, now(), #{regAdmSq}
      )
