mapper.select:
SELECT A.*, (SELECT CODENAME FROM TB_CODE WHERE CODE=A.STAT AND GROUPID='ADMSTAT') AS CODENAME, DATE_FORMAT(REG_DTTM, '%Y-%m-%d %H:%i') AS REG_DTTM1, (SELECT BTBS_SITE_NM FROM TB_BTBS WHERE BTBS_SQ = A.BTBS_SQ) AS BTBS_SITE_NM FROM TB_BTB_ADM A WHERE 1=1 AND BTBS_SQ != #{btbs_sq}
AND A.REG_DTTM BETWEEN CONCAT(#{start_dt},'000000') AND CONCAT(#{end_dt},'235959')
AND A.ADM_EMAIL LIKE CONCAT('%', #{keyword}, '%') AND A.ADM_NM LIKE CONCAT('%', #{keyword}, '%') AND EXISTS (SELECT 0 FROM TB_BTBS WHERE BTBS_SQ = A.BTBS_SQ AND BTBS_SITE_NM LIKE CONCAT('%', #{keyword}, '%'))
ORDER BY ${orderBy} LIMIT ${R_PAGE} , ${R_ROW}

SELECT COUNT(*) AS CNT FROM TB_BTB_ADM A WHERE 1=1 AND BTBS_SQ != #{btbs_sq}
AND A.REG_DTTM BETWEEN CONCAT(#{start_dt},'000000') AND CONCAT(#{end_dt},'235959')
AND A.ADM_EMAIL LIKE CONCAT('%', #{keyword}, '%') AND A.ADM_NM LIKE CONCAT('%', #{keyword}, '%') AND EXISTS (SELECT 0 FROM TB_BTBS WHERE BTBS_SQ = A.BTBS_SQ AND BTBS_SITE_NM LIKE CONCAT('%', #{keyword}, '%'))

SELECT  *,
        DATE_FORMAT(DTTM, '%Y-%m-%d %H:%i') AS DTTM1 
FROM    TB_ADMIN_ACT_LOG 
WHERE   1=1 
AND     ADM_EMAIL = #{adm_email}
ORDER BY ${orderBy}
LIMIT ${R_PAGE} , ${R_ROW}
