mapper.update:
    UPDATE TB_POP SET 
		POP_NM = #{R_POP_NM} 
		,POP_CONT  = #{R_POP_CONT} 
		,BEGIN_DTTM  = #{R_BEGIN_DTTM} 
		,END_DTTM  = #{R_END_DTTM} 
		,LNK_URL  = #{R_LNK_URL} 
		,POP_IMG_FILE_NM = #{R_POP_IMG_FILE_NM} 
		,POP_IMG_PATH = #{R_POP_IMG_PATH} 
		,POP_TP = #{R_POP_TP} 
		,POP_WIDTH = #{R_POP_WIDTH} 
		,POP_HEIGHT = #{R_POP_HEIGHT} 
		,STAT = #{R_STAT} 
		,UPT_DTTM = now()
		,UPT_ADM_SQ = #{R_UPT_ADM_SQ}
    WHERE 1=1 
    AND POP_SQ = #{R_POP_SQ}

    UPDATE TB_POP SET 
		STAT = '004'
		,DEL_DTTM = now()
		,DEL_ADM_EMAIL = #{R_DEL_ADM_EMAIL}
		,DEL_RSN = #{R_DEL_RSN}
    WHERE 1=1 
    AND POP_SQ = #{R_POP_SQ}

mapper.insert:
INSERT INTO TB_POP( 
	POP_SQ
	,POP_NM
	,POP_CONT
	,BEGIN_DTTM
	,END_DTTM
	,LNK_URL
	,POP_IMG_FILE_NM
	,POP_IMG_PATH
	,POP_TP
	,POP_WIDTH
	,POP_HEIGHT
	,STAT
	,REG_DTTM
	,REG_ADM_SQ
	,BTBS_SQ
) VALUES (
	NEXTVAL('POP_SQ')
	,#{R_POP_NM}
	,#{R_POP_CONT}
	,#{R_BEGIN_DTTM}
	,#{R_END_DTTM}
	,#{R_LNK_URL}
	,#{R_POP_IMG_FILE_NM}
	,#{R_POP_IMG_PATH}
	,#{R_POP_TP}
	,#{R_POP_WIDTH}
	,#{R_POP_HEIGHT}
	,#{R_STAT}
	,NOW()
	,#{R_REG_ADM_SQ}
	,#{R_BTBS_SQ}
)

mapper.update:
    UPDATE TB_ENV_INFO SET 
    	UPT_DTTM = now()
    	,UPT_ADM_SQ = #{R_UPT_ADM_SQ}

			,ID = #{R_ID}
			,PWD = #{R_PWD}
			,HOST = #{R_HOST}
			,PORT = #{R_PORT}
			,SSL_YN = #{R_SSL_YN}
			,SSL_PORT = #{R_SSL_PORT}


			,ATO_MIN = #{R_ATO_MIN}
			,ATO_MAX = #{R_ATO_MAX}

    WHERE 1=1
