mapper.select:
SELECT A.MEM_SQ, A.MEM_EMAIL, A.MEM_NM, CONCAT(A.ADDR_1,' ',A.ADDR_2) AS ADDR, A.USER_HP, DATE_FORMAT(A.REG_DTTM, '%Y-%m-%d') AS REG_DTTM, (SELECT CODENAME FROM TB_CODE WHERE GROUPID = 'MEMSTAT' AND CODE = A.STAT) STATCODE, (SELECT CODENAME FROM TB_CODE WHERE GROUPID = 'GENDER' AND CODE = A.MEM_GEN) GENCODE, A.BTBS_SQ, (SELECT DISTINCT(BTBS_SITE_NM) FROM TB_BTBS WHERE BTBS_SQ = A.BTBS_SQ) AS BTBS_NM FROM TB_MEM A WHERE 1=1
AND A.BTBS_SQ = #{btbsSq}
AND A.ADDR_1 = (SELECT CODENAME FROM TB_CODE WHERE CODE = #{addr_1})
AND A.ADDR_2 = (SELECT CODENAME FROM TB_CODE WHERE CODE = #{addr_2})
AND A.ADDR_3 = (SELECT CODENAME FROM TB_CODE WHERE CODE = #{addr_3})
AND A.MEM_GEN = #{memGen}
AND REG_DTTM BETWEEN CONCAT(#{startDate},' 00:00:00') AND CONCAT(#{endDate},' 23:59:59')
AND A.MEM_EMAIL LIKE CONCAT('%',#{keyWord},'%')
AND A.MEM_NM LIKE CONCAT('%',#{keyWord},'%')
ORDER BY ${orderBy} LIMIT ${R_PAGE} , ${R_ROW}

mapper.update:
UPDATE TB_MEM SET
	MEM_PWD = PASSWORD(#{memPwd}),
	UPT_DTTM = NOW(),
	UPT_ADM_SQ = #{uptAdmSq}
WHERE MEM_SQ = #{memSq}

mapper.select:
SELECT
	A.*,
	DATE_FORMAT(A.DTTM, '%Y-%m-%d %h:%m:%s') AS REG_DTTM,
	(SELECT HP_MD_NM FROM TB_PUSH_USER_INFO WHERE MEM_SQ = (SELECT MEM_SQ FROM TB_MEM WHERE MEM_EMAIL = A.USER_EMAIL)) AS MODEL,
	(SELECT HP_OS FROM TB_PUSH_USER_INFO WHERE MEM_SQ = (SELECT MEM_SQ FROM TB_MEM WHERE MEM_EMAIL = A.USER_EMAIL)) AS OS
FROM TB_USER_ACT_LOG A
	WHERE A.USER_EMAIL = #{memEmail}

		AND DTTM BETWEEN CONCAT(#{startDate},' 00:00:00') AND CONCAT(#{endDate},' 23:59:59')

	ORDER BY ${orderBy}
	LIMIT ${R_PAGE} , ${R_ROW}

SELECT BTBS_SQ, BTBS_SITE_NM, BTBS_COMP_NM, BTBS_DOMAIN FROM TB_BTBS WHERE 1=1
AND BTBS_SITE_NM LIKE CONCAT('%', #{btbsNm}, '%')
ORDER BY ${orderBy} LIMIT ${R_PAGE} , ${R_ROW}

SELECT (SELECT COUNT(*) FROM TB_MEM WHERE MEM_GEN='M'
AND BTBS_SQ = #{btbsSq}
AND REG_DTTM BETWEEN CONCAT(#{startDate},' 00:00:00') AND CONCAT(#{endDate},' 23:59:59')
) AS MALE, (SELECT COUNT(*) FROM TB_MEM WHERE MEM_GEN='F'
AND BTBS_SQ = #{btbsSq}
AND REG_DTTM BETWEEN CONCAT(#{startDate},' 00:00:00') AND CONCAT(#{endDate},' 23:59:59')
) AS FEMALE FROM DUAL
